const axios = require("axios");

const API_BASE_URL = 'https://api.daydev.lol'
//Define the  module Structure
const api = {
  hi: {
    sayhi: () => {
      return 'Hello! I am NPM Package: BitsToKB API';
      
    }
  },
  frc: {
    pickupline: async () => {
      try{
        const axiosInstance = createAxiosInstance();
        const response = await axiosInstance.get('/frcpickup')
        return response.data;
  
      } catch (error) {
        throw new Error(`Failed to fetch data from API! Error Code: ${error.message}`);
      }
    },
  },
};

module.exports = api;

function createAxiosInstance() {
  return axios.create({
    baseURL: API_BASE_URL,
  });
}
